tmpls.afmt:
{{#En-tête}}<div>{{En-tête}}</div>{{/En-tête}}
<div style="display: none">{{cloze:À Remplir}}</div>
<div id="err"></div>
<div id="image-occlusion-container">
    {{Image}}
    <canvas id="image-occlusion-canvas"></canvas>
</div>
<script>
try {
    anki.imageOcclusion.setup();
} catch (exc) {
    document.getElementById("err").innerHTML = `Une erreur est survenue en chargeant le schéma à remplir. Votre version d'Anki est-elle à jour ?<br><br>${exc}`;
}
</script>

<div><button id="toggle">Activer/Désactiver les Masques</button></div>
{{#Verso Extra}}<div>{{Verso Extra}}</div>{{/Verso Extra}}

